import React from 'react';
import Link from 'next/link'
import styled from 'styled-components';
// material
import Divider from '@material-ui/core/Divider';
import Grid from '@material-ui/core/Grid';
// firebase
import { auth } from '../../firebase';
// components
import { H1, H2 } from '../../components/Typography';
import { StyledButton } from '../../components/StyledButton';
import { StyledTextField } from '../../components/StyledTextField';

// styles
const MainLayout = styled.div`
  display: flex;
  align-items: flex-end;
  height: 100vh;
  background: #bbbbbb;
  background: url('https://raw.githubusercontent.com/EdwardGoomba/imgHost/master/crmBeta/bg.png');
`;

const Branding = styled.div`
  color: #ffffff;
  margin-left: 3rem;
  margin-bottom: 5rem;
  max-width: 300px;
`;

// TODO: add responsive styles

// styles
const Layout = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
`;

const LoginWrapper = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  width: 350px;
`;

const FormWrapper = styled.form`
  display: flex;
  flex-direction: column;
`;

const Actions = styled.div`
  display: flex;
  justify-content: space-between;
`;

const Login = () => {
  const [state, setState] = React.useState({
    email: '',
    password: '',
  });

  const handleChange = name => event => {
    setState({ ...state, [name]: event.target.value });
  };

  const handleSubmit = event => {
    event.preventDefault();
    auth
      .signInWithEmailAndPassword(state.email, state.password)
      .then(authUser => {
        // TODO: remove temp store user id in localstorage to immitate login
        console.log('Login Success');
        localStorage.setItem('AUTH_TOKEN', authUser.user.uid);
      })
      .catch(error => console.log('Error Loggin In: ', error));
    // TODO: setup login mutation
    // TODO: add better error handling
    // TODO: alert users on errors in ui
  };

  return (
    <Grid container>
      <Grid item xs={6}>
        <MainLayout>
          <Branding>
            <H1>CRM Beta</H1>
            {/* <H2>Customer Relationship Management</H2> */}
          </Branding>
        </MainLayout>
      </Grid>
      <Grid item xs={6}>
        <Layout>
          <LoginWrapper>
            <H1>Login</H1>
            <FormWrapper onSubmit={event => handleSubmit(event)}>
              <StyledTextField
                id="outlined-basic"
                label="Email"
                margin="normal"
                variant="outlined"
                value={state.email}
                onChange={handleChange('email')}
              />
              <StyledTextField
                id="outlined-basic"
                label="Password"
                margin="normal"
                variant="outlined"
                value={state.password}
                onChange={handleChange('password')}
              />
              <StyledButton variant="contained" type="submit">
                Login
          </StyledButton>
            </FormWrapper>
            <Divider style={{ marginBottom: 15 }} />
            <Actions>
              <Link
                href="/auth/signup"
              // style={{
              //   textDecoration: 'none',
              // }}
              >
                <a>
                  Need an account?
            </a>
              </Link>
              <Link
                href="/auth/reset"
              // style={{
              //   textDecoration: 'none',
              // }}
              >
                <a>
                  Forgot password?
            </a>
              </Link>
            </Actions>
          </LoginWrapper>
        </Layout>
      </Grid>
    </Grid>

  );
};

export default Login;
